// handles data received from UART
static void uart_process_line(FuriString* line, void* context) {
    SpotifyRemoteApp* app = context;
    furi_string_set(app->message_recieved, line);
    scene_manager_handle_custom_event(app->scene_manager, SPOTIFY_REMOTE_ON_RECEIVE_EVENT);
}

// action taken based on submenu option
void spotify_remote_main_menu_callback(void* context, uint32_t index) {
    SpotifyRemoteApp* app = context;
    switch(index) {
    case SPOTIFY_REMOTE_MAIN_MENU_SCENE_LAUNCH:
        app->is_remote_launched = true;
        scene_manager_handle_custom_event(
            app->scene_manager, SPOTIFY_REMOTE_MAIN_MENU_SCENE_LAUNCH_EVENT);
        break;
    case SPOTIFY_REMOTE_MAIN_MENU_SCENE_CONFIG:
        scene_manager_handle_custom_event(
            app->scene_manager, SPOTIFY_REMOTE_MAIN_MENU_SCENE_CONFIG_EVENT);
        break;
    case SPOTIFY_REMOTE_MAIN_MENU_SCENE_ABOUT:
        scene_manager_handle_custom_event(
            app->scene_manager, SPOTIFY_REMOTE_MAIN_MENU_SCENE_ABOUT_EVENT);
        break;
    default:
        break;
    }
}

// main menu scene manager callbacks
void spotify_remote_main_menu_scene_on_enter(void* context) {
    SpotifyRemoteApp* app = context;
    submenu_reset(app->main_menu);
    submenu_set_header(app->main_menu, "Spotify Remote");

    submenu_add_item(
        app->main_menu,
        "Launch Remote",
        SPOTIFY_REMOTE_MAIN_MENU_SCENE_LAUNCH,
        spotify_remote_main_menu_callback,
        app);
    submenu_add_item(
        app->main_menu,
        "Config",
        SPOTIFY_REMOTE_MAIN_MENU_SCENE_CONFIG,
        spotify_remote_main_menu_callback,
        app);
    submenu_add_item(
        app->main_menu,
        "About",
        SPOTIFY_REMOTE_MAIN_MENU_SCENE_ABOUT,
        spotify_remote_main_menu_callback,
        app);

    view_dispatcher_switch_to_view(app->view_dispatcher, SPOTIFY_REMOTE_MAIN_MENU_VIEW);
}

bool spotify_remote_main_menu_scene_on_event(void* context, SceneManagerEvent event) {
    SpotifyRemoteApp* app = context;
    bool consumed = false;

    switch(event.type) {
    case SceneManagerEventTypeCustom:
        switch(event.event) {
        case SPOTIFY_REMOTE_MAIN_MENU_SCENE_LAUNCH_EVENT:
            scene_manager_next_scene(app->scene_manager, SPOTIFY_REMOTE_LOADING_SCENE);
            consumed = true;
            break;
        case SPOTIFY_REMOTE_MAIN_MENU_SCENE_CONFIG_EVENT:
            scene_manager_next_scene(app->scene_manager, SPOTIFY_REMOTE_WIFI_SSID_SCENE);
            consumed = true;
            break;
        case SPOTIFY_REMOTE_MAIN_MENU_SCENE_ABOUT_EVENT:
            scene_manager_next_scene(app->scene_manager, SPOTIFY_REMOTE_ABOUT_SCENE);
            consumed = true;
            break;
        }
        break;
    default:
        break;
    }

    return consumed;
}

void spotify_remote_main_menu_scene_on_exit(void* context) {
    SpotifyRemoteApp* app = context;
    submenu_reset(app->main_menu);
}

// loading scene manager callbacks
void spotify_remote_loading_scene_on_enter(void* context) {
    SpotifyRemoteApp* app = context;
    view_dispatcher_switch_to_view(app->view_dispatcher, SPOTIFY_REMOTE_LOADING_VIEW);
}

bool spotify_remote_loading_scene_on_event(void* context, SceneManagerEvent event) {
    SpotifyRemoteApp* app = context;
    bool consumed = false;

    switch(event.type) {
    case SceneManagerEventTypeCustom:
        switch(event.event) {
        case SPOTIFY_REMOTE_ON_RECEIVE_EVENT:
            if(furi_string_get_cstr(app->message_recieved)[0] == 'I' &&
               furi_string_get_cstr(app->message_recieved)[1] == 'P') {
                scene_manager_next_scene(app->scene_manager, SPOTIFY_REMOTE_IP_SCENE);
                consumed = true;
            }
            break;
        }
    default:
        break;
    }

    return consumed;
}

void spotify_remote_loading_scene_on_exit(void* context) {
    UNUSED(context);
}

// ip scene callbacks
void spotify_remote_ip_scene_on_enter(void* context) {
    SpotifyRemoteApp* app = context;
    text_box_reset(app->ip);
    text_box_set_text(app->ip, furi_string_get_cstr(app->message_recieved));
    text_box_set_font(app->ip, TextBoxFontText);
    view_dispatcher_switch_to_view(app->view_dispatcher, SPOTIFY_REMOTE_IP_VIEW);
}

bool spotify_remote_ip_scene_on_event(void* context, SceneManagerEvent event) {
    SpotifyRemoteApp* app = context;
    bool consumed = false;

    switch(event.type) {
    case SceneManagerEventTypeCustom:
        switch(event.event) {
        case SPOTIFY_REMOTE_ON_RECEIVE_EVENT:
            if(furi_string_get_cstr(app->message_recieved)[0] == 'O' &&
               furi_string_get_cstr(app->message_recieved)[1] == 'K') {
                scene_manager_next_scene(app->scene_manager, SPOTIFY_REMOTE_BUTTON_PANEL_SCENE);
                consumed = true;
            }
            break;
        }
    default:
        break;
    }

    return consumed;
}

void spotify_remote_ip_scene_on_exit(void* context) {
    SpotifyRemoteApp* app = context;
    text_box_reset(app->ip);
}

// called when selection is made in button panel
void button_panel_on_select_callback(void* context, uint32_t index) {
    SpotifyRemoteApp* app = context;
    switch(index) {
    case PREV:
        uart_helper_send(app->uart_helper, "1\n", 2);
        break;
    case NEXT:
        uart_helper_send(app->uart_helper, "2\n", 2);
        break;
    case PLAY:
        uart_helper_send(app->uart_helper, "3\n", 2);
        break;
    case PAUSE:
        uart_helper_send(app->uart_helper, "4\n", 2);
        break;
    case SHUFFLE:
        uart_helper_send(app->uart_helper, "5\n", 2);
        break;
    case REPEAT:
        uart_helper_send(app->uart_helper, "6\n", 2);
        break;
    default:
        break;
    }
}

